let BaseComponent = require("@jatahworx/bhive-toolkits").BaseComponent;

module.exports = class Log extends BaseComponent {
    constructor(constructorOptions) {
        super(constructorOptions.id, constructorOptions.name, "neutrinos_service_designer_Log", "Log");
        this.logObject = constructorOptions.logObject;
		this.functionName = constructorOptions.functionName;
        this.viewType = 'client';
    }

    get logObject() {
        return this._logObject;
    }

    set logObject(logObject) {
        this._logObject = logObject;
    }

    get functionName() {
        return this._functionName;
    }

    set functionName(functionName) {
        this._functionName = functionName;
    }

    generateSnippet() {
        return `console.log(new Date().toLocaleTimeString(), ${this.logObject});`;
	}
	
	getCallTemplate() {
		return `this.${this.functionName}(bh);`;
	}
};