let BaseComponent = require("@jatahworx/bhive-toolkits").BaseComponent;

module.exports = class Comments extends BaseComponent {
    constructor(constructorOptions) {
        super(constructorOptions.id, constructorOptions.name, "neutrinos_service_designer_comments", "Comments");
        this.comments = constructorOptions.comments;
        this.viewType = 'common';
    }

    get comments() {
        return this._comments;
    }

    set comments(comments) {
        this._comments = comments;
    }

    generateSnippet() {
        return ("/*" + this.comments + "*/");
    }
}