let BaseComponent = require("@jatahworx/bhive-toolkits").BaseComponent;
const SRDCodegenUtil = require('@jatahworx/bhive-toolkits').SRDCodeGenUtil;

module.exports = class Script extends BaseComponent {
	constructor(constructorOptions) {
		super(constructorOptions.id, constructorOptions.name, 'neutrinos_service_designer_script', 'Script');
		this.constructorOptions = constructorOptions;
		this.nodeID = constructorOptions.id;
		this.viewType = 'common';
	}

	init() {
		this.script = this.constructorOptions.script;
		this.cgUtil = new SRDCodegenUtil();
	}

	get script() {
		return this._script;
	}

	set script(script) {
		this._script = script;
	}

	get functionName() {
		return this._functionName;
	}

	set functionName(functionName) {
		this._functionName = functionName;
	}

	generateSnippet() {
		this.init();
		return this.script;
	}
};
