const SRDCodeGenUtil = require('@jatahworx/bhive-toolkits').SRDCodeGenUtil;
module.exports = class Variable {
    constructor(constructorOptions) {
        this.srdCodeGenUtil = new SRDCodeGenUtil();
        this.key = constructorOptions.key;
        this.defaultValue = constructorOptions.defaultValue;
        this.type = constructorOptions.defaultValue.type;
        this.output = constructorOptions.output;
    }

    set key(key) {
        this._key = key;
    }

    get key() {
        return this._key;
    }

    set defaultValue(defaultValue) {
        if (typeof defaultValue === 'string') {
            defaultValue = {
                type: 'str',
                value: defaultValue
            };
        }
        this._defaultValue = this.srdCodeGenUtil.getValueFromTypedInput(defaultValue);
    }
    get defaultValue() {
        return this._defaultValue;
    }

    get output() {
        return this._output;
    }

    set output(output) {
        if (typeof output === "boolean") {
            this._output = output;
        }
        else {
            throw "output property " + this.key + " must be a Boolean";
        }
    }
}