let BaseComponent = require("@jatahworx/bhive-toolkits").BaseComponent;
let DBManager = require('./util/DBManager');

// let TLS = require('../config/TLS');

module.exports = class DBConfig extends BaseComponent {
    static type = "db-config";
    constructor(constructorOptions) {
        super(constructorOptions.id, constructorOptions.name, "neutrinos_service_designer_http", "db-config");
    }

    updatePackageJSON(selectedDB) {
        let npmSqlModule = DBManager.getPackageDetails(selectedDB);
        return [npmSqlModule];
    }
}