let constants = require('./Constants');
module.exports = class DBManager{
/*
    This class should control DB config, and other related db operations 
*/
    static getPackageDetails(dbType){
        switch(dbType){
            case constants.CONST_DB_TYPE_MARIADB:
            case constants.CONST_DB_TYPE_MYSQL:
                return {name:'mysql',version:'2.17.1'};
            case constants.CONST_DB_TYPE_POSTGRES:
                return {name:'pg',version:'7.17.1'};
            case constants.CONST_DB_TYPE_MSSQL:
                return {name:'mssql',version: '6.0.1'};
            case constants.CONST_DB_TYPE_ORACLE_DB:
                return {name: 'oracledb',version: '4.2.0'}
        }
    }
}