"use strict";
let AdvancedComponent = require("../../../core/AdvancedComponent");
let Attribute = require('../../../core/Attributes');

module.exports = class InputSuffixIcon extends AdvancedComponent {
    constructor() {
        const name = "ng-input-suffix-icon";
        const designerTemplate = '<ng-input-suffix-icon></ng-input-suffix-icon>';
        const paletteTemplate = 'Input Suffix Icon';
        const componentLabel = 'Input SUffix Icon';

        super({
            name,
            designerTemplate,
            paletteTemplate,
            componentLabel,
            isAdvancedChild: true,
            templateUrl: "articles/#!components-guide-7/input"
        });
        super.addAttribute(new Attribute({
            key: 'Suffix Icon',
            value: '',
            type: 'a',
            visible: false,
            isVisibleForParent: true
        }));
        super.addAttribute(new Attribute({
            key: '(suffixIconClick)',
            value: '',
            type: 'a',
            visible: false,
            isVisibleForParent: true
        }));
        super.composeTemplate();
    }

    get template() {
        let returnTemplate = '';
        let componentAttributes = this.getHtmlAttributes(this.htmlAttributes);

        if (componentAttributes['Suffix Icon']._value.trim() != "" && componentAttributes['(suffixIconClick)']._value == "") {
            returnTemplate = `<mat-icon matSuffix>${componentAttributes['Suffix Icon']._value}</mat-icon>`;
        } else if (componentAttributes['Suffix Icon']._value.trim() != "" && componentAttributes['(suffixIconClick)']._value.trim() != "") {
            returnTemplate = `<mat-icon matSuffix (click)="${componentAttributes['(suffixIconClick)']._value}">${componentAttributes['Suffix Icon']._value}</mat-icon>`;
        }
        return returnTemplate;
    }

    set template(t) {}
};