let ParagraphAlignInstance = null;
module.exports = class align {

    constructor() {
        if (!ParagraphAlignInstance) {
            ParagraphAlignInstance = this;
            //set all other default values

            this.displayAs = 'Align';
            this.value = 'align'
            this.type = 'DROPDOWN';
            this.values = [
                { display: "Left", value: "left", default: true},                
                { display: "Center", value: "center"},
                { display: "Right", value: "right"},
                { display: "Justify", value: "justify"}

            ];
        }

        return ParagraphAlignInstance;
    }
}