let appearanceInstance = null;
module.exports = class Appearance {

    constructor() {
        if (!appearanceInstance) {
            appearanceInstance = this;
            //set all other default values

            this.displayAs = 'Form Field Appearance';
            this.value = '[appearance]'
            this.type = 'DROPDOWN';
            this.values = [
                { display: "Legacy", value: "'legacy'" },
                { display: "Standard ", value: "'standard'"},
                { display: "Fill", value: "'fill'" },
                { display: "Outline", value: "'outline'" }
            ];
        }

        return appearanceInstance;
    }
}