let audioPlayLoopInstance = null;
module.exports = class playLoop {

    constructor() {
        if (!audioPlayLoopInstance) {
            audioPlayLoopInstance = this;
            //set all other default values

            this.displayAs = 'Loop';
            this.value = '[loop]'
            this.type = 'TOGGLE';
            this.values = {'true-value':'true', 'false-value': 'false'}
        }
        return audioPlayLoopInstance;
    }
}