let autoActiveFirstOptionInstance = null;
module.exports = class autoFocus {

    constructor() {
        if (!autoActiveFirstOptionInstance) {
            autoActiveFirstOptionInstance = this;
            //set all other default values

            this.displayAs = 'Auto Active First Option';
            this.value = 'autoActiveFirstOption'
            this.type = 'TOGGLE';
            this.values = {'true-value':'autoActiveFirstOption', 'false-value': ''}
        }
        return autoActiveFirstOptionInstance;
    }
}