let autoFocusInstance = null;
module.exports = class autoFocus {

    constructor() {
        if (!autoFocusInstance) {
            autoFocusInstance = this;
            //set all other default values

            this.displayAs = 'Auto Focus';
            this.value = 'autoFocus'
            this.type = 'TOGGLE';
            this.values = {'true-value':'true', 'false-value': 'false'}
        }
        return autoFocusInstance;
    }
}