let AutoScaleInstance = null;
module.exports = class autoScale {

    constructor() {
        if (!AutoScaleInstance) {
            AutoScaleInstance = this;
            //set all other default values

            this.displayAs = 'AutoScale';
            this.value = '[autoScale]'
            this.type = 'TOGGLE';
            this.values = {'true-value':'true', 'false-value': 'false'}
        }
        return AutoScaleInstance;
    }
}