let autoSizeInstance = null;
module.exports = class autosize {

    constructor() {
        if (!autoSizeInstance) {
            autoSizeInstance = this;
            //set all other default values

            this.displayAs = 'Auto Size';
            this.value = 'autosize'
            this.type = 'TOGGLE';
            this.values = {'true-value':'true', 'false-value': 'false'}
        }
        return autoSizeInstance;
    }
}