let autoPlayInstance = null;
module.exports = class autoPlay {

    constructor() {
        if (!autoPlayInstance) {
            autoPlayInstance = this;
            //set all other default values

            this.displayAs = 'Auto Play';
            this.value = '[autoplay]'
            this.type = 'TOGGLE';
            this.values = {'true-value':'true', 'false-value': 'false'}
        }
        return autoPlayInstance;
    }
}