let cardTitleGroupImagesInstance = null;
module.exports = class cardTitleGroupImages {

    constructor() {
        if (!cardTitleGroupImagesInstance) {
            cardTitleGroupImagesInstance = this;
            //set all other default values

            this.displayAs = 'Image Size';
            this.value = 'imageType'
            this.type = 'DROPDOWN';
            this.values = [
                { display: "Small", value: "sm"},
                { display: "Medium", value: "md" },
                { display: "Large", value: "lg" },

            ];
        }

        return cardTitleGroupImagesInstance;
    }
}