let checkedInstance = null;
module.exports = class checked {

    constructor() {
        if (!checkedInstance) {
            checkedInstance = this;
            //set all other default values

            this.displayAs = 'Checked';
            this.value = '[checked]'
            this.type = 'TOGGLE';
            this.values = {'true-value':'true', 'false-value': 'false'}
        }
        return checkedInstance;
    }
}