let ColorInstance = null;
module.exports = class color {

    constructor() {
        if (!ColorInstance) {
            ColorInstance = this;
            //set all other default values

            this.displayAs = 'Color';
            this.value = 'color'
            this.type = 'DROPDOWN';
            this.values = [
                { display: "None", value: ""},                
                { display: "Primary", value: "primary", default: true },
                { display: "Accent", value: "accent"},
                { display: "Warn", value: "warn"}

            ];
        }

        return ColorInstance;
    }
}