module.exports = class CssClass {
	/**
	 * @param {string} displayAs
	 */
	constructor(displayAs) {
		//set all other default values
		this.displayAs = displayAs;
		this.value = '';
		this.type = 'AUTOCOMPLETE';
		/**
		 * @param {any} value
		 * @param {string | any[]} list
		 */
		this.values = {
			readFrom: 'CLASSES', validate: (value, list) => {
				if (list instanceof Array && list.includes(value)) {
					return false;
				}
				return true;
			}
		};
	}
};
