let datePickertouchUiInstance = null;
module.exports = class datePickerTouchUi {

    constructor() {
        if (!datePickertouchUiInstance) {
            datePickertouchUiInstance = this;
            //set all other default values

            this.displayAs = 'Touch UI';
            this.value = 'touchUi'
            this.type = 'TOGGLE';
            this.values = {'true-value':'true', 'false-value': 'false'}
        }
        return datePickertouchUiInstance;
    }
}