let datepickerIconInstance = null;
module.exports = class datepickerIconPosition {

    constructor() {
        if (!datepickerIconInstance) {
            datepickerIconInstance = this;
            //set all other default values

            this.displayAs = 'Icon Position';
            this.value = '[iconPosition]'
            this.type = 'TOGGLE';
            this.labels = {'true-label': 'Prefix', 'false-label': 'Suffix'}
            this.values = {'true-value':'matPrefix', 'false-value': 'matSuffix'}
        }
        return datepickerIconInstance;
    }
}