let disableCloseInstance = null;
module.exports = class disableClose {

    constructor() {
        if (!disableCloseInstance) {
            disableCloseInstance = this;
            //set all other default values

            this.displayAs = 'Disable Close';
            this.value = 'disableClose'
            this.type = 'TOGGLE';
            this.values = {'true-value':'true', 'false-value': 'false'}
        }
        return disableCloseInstance;
    }
}