let disabledDatePickerInstance = null;
module.exports = class disabledDatePicker {

    constructor() {
        if (!disabledDatePickerInstance) {
            disabledDatePickerInstance = this;
            //set all other default values

            this.displayAs = 'Disabled Datepicker';
            this.value = '[disabledDatepicker]'
            this.type = 'TOGGLE';
            this.values = {'true-value':'true', 'false-value': 'false'}
        }
        return disabledDatePickerInstance;
    }
}