let disabledDatePickerInputInstance = null;
module.exports = class disabledDatePickerInput {

    constructor() {
        if (!disabledDatePickerInputInstance) {
            disabledDatePickerInputInstance = this;
            //set all other default values

            this.displayAs = 'Disabled Input';
            this.value = '[disabledDatepickerInput]'
            this.type = 'TOGGLE';
            this.values = {'true-value':'true', 'false-value': 'false'}
        }
        return disabledDatePickerInputInstance;
    }
}