let disableDragValueInstance = null;
module.exports = class disableDragValue {

    constructor() {
        if (!disableDragValueInstance) {
            disableDragValueInstance = this;
            //set all other default values

            this.displayAs = 'Disable Drag Value';
            this.value = '[disableDragValue]'
            this.type = 'TOGGLE';
            this.values = {'true-value':'true', 'false-value': 'false'}
        }
        return disableDragValueInstance;
    }
}