let disableRippleInstance = null;
module.exports = class disableRipple {

    constructor() {
        if (!disableRippleInstance) {
            disableRippleInstance = this;
            //set all other default values

            this.displayAs = 'Disable Ripple';
            this.value = '[disableRipple]'
            this.type = 'TOGGLE';
            this.values = {'true-value':'true', 'false-value': 'false'}
        }
        return disableRippleInstance;
    }
}