let disableToggleValueInstance = null;
module.exports = class disableToggleValue {

    constructor() {
        if (!disableToggleValueInstance) {
            disableToggleValueInstance = this;
            //set all other default values

            this.displayAs = 'Disable Toggle Value';
            this.value = '[disableToggleValue]'
            this.type = 'TOGGLE';
            this.values = {'true-value':'true', 'false-value': 'false'}
        }
        return disableToggleValueInstance;
    }
}