let doughnutInstance = null;
module.exports = class doughnut {

    constructor() {
        if (!doughnutInstance) {
            doughnutInstance = this;
            //set all other default values

            this.displayAs = 'Doughnut';
            this.value = '[doughnut]'
            this.type = 'TOGGLE';
            this.values = {'true-value':'true', 'false-value': 'false'}
        }
        return doughnutInstance;
    }
}