let dynaicHeightInstance = null;
module.exports = class dynaicHeight {

    constructor() {
        if (!dynaicHeightInstance) {
            dynaicHeightInstance = this;
            //set all other default values

            this.displayAs = 'Dynamic Height';
            this.value = 'dynaicHeight'
            this.type = 'TOGGLE';
            this.values = {'true-value':'true', 'false-value': 'false'}
        }
        return dynaicHeightInstance;
    }
}