let dynamicTabInstance = null;
module.exports = class dynamicTab {

    constructor() {
        if (!dynamicTabInstance) {
            dynamicTabInstance = this;
            //set all other default values

            this.displayAs = 'Dynamic Tab';
            this.value = 'dynamicTab'
            this.type = 'TOGGLE';
            this.values = {'true-value':'dynamic', 'false-value': 'static'}
        }
        return dynamicTabInstance;
    }
}