let editableInstance = null;
module.exports = class editable {

    constructor() {
        if (!editableInstance) {
            editableInstance = this;
            //set all other default values

            this.displayAs = 'Editable';
            this.value = 'editable'
            this.type = 'TOGGLE';
            this.values = {'true-value':'true', 'false-value': 'false'}
        }
        return editableInstance;
    }
}