let errorStateInstance = null;
module.exports = class errorState {

    constructor() {
        if (!errorStateInstance) {
            errorStateInstance = this;
            //set all other default values

            this.displayAs = 'Error State';
            this.value = 'errorState'
            this.type = 'TOGGLE';
            this.values = {'true-value':'true', 'false-value': 'false'};
        }
        return errorStateInstance;
    }
}