let explodeSlicesInstance = null;
module.exports = class explodeSlices {

    constructor() {
        if (!explodeSlicesInstance) {
            explodeSlicesInstance = this;
            //set all other default values

            this.displayAs = 'Explode Slices';
            this.value = '[explodeSlices]'
            this.type = 'TOGGLE';
            this.values = {'true-value':'true', 'false-value': 'false'}
        }
        return explodeSlicesInstance;
    }
}