let FXDirInstance = null;
module.exports = class FXDir {

    constructor() {
        if (!FXDirInstance) {
            FXDirInstance = this;
            //set all other default values

            this.displayAs = 'Direction';
            this.value = 'dir'
            //TODO - change this to Radio
            this.type = 'DROPDOWN';
            this.values = [
                { display: "left 2 right", value: "ltr" },
                { display: "right 2 left ", value: "rtl" }
            ];
        }
        return FXDirInstance;
    }
}
