let FXFillInstance = null;
module.exports = class FXFill {

    constructor() {
        if (!FXFillInstance) {
            FXFillInstance = this;
            //set all other default values

            this.displayAs = 'Layout Fill';
            this.value = 'fxFill'
            this.type = 'TOGGLE';
            this.labels = {'true-label': 'Fill', 'false-label': 'No-fill'}
            this.values = {'true-value':'fxFill', 'false-value': ''}
        }
        return FXFillInstance;
    }
}
