let fixedInViewPortInstance = null;
module.exports = class fixedInViewport {

    constructor() {
        if (!fixedInViewPortInstance) {
            fixedInViewPortInstance = this;
            //set all other default values

            this.displayAs = 'Fixed InView Port';
            this.value = 'fixedInViewport'
            this.type = 'TOGGLE';
            this.values = {'true-value':'true', 'false-value': 'false'}
        }
        return fixedInViewPortInstance;
    }
}