let floatLabelInstance = null;
module.exports = class floatLabel {

    constructor() {
        if (!floatLabelInstance) {
            floatLabelInstance = this;
            this.displayAs = 'Float Label';
            this.value = '[floatLabel]'
            this.type = 'DROPDOWN';
            this.values = [
                { display: "Auto", value: "'auto'" },
                { display: "Never", value: "'never'" },
                { display: "Always", value: "'always'" },
            ];
        }
        return floatLabelInstance;
    }
}