let focusedInstance = null;
module.exports = class focused {

    constructor() {
        if (!focusedInstance) {
            focusedInstance = this;
            //set all other default values

            this.displayAs = 'Focused';
            this.value = 'focused'
            this.type = 'TOGGLE';
            this.values = {'true-value':'true', 'false-value': 'false'};
        }
        return focusedInstance;
    }
}