let selectGroupOptionsInstance = null;
module.exports = class groupOptions {

    constructor() {
        if (!selectGroupOptionsInstance) {
            selectGroupOptionsInstance = this;
            //set all other default values

            this.displayAs = 'Group Options';
            this.value = '[groupOptions]'
            this.type = 'TOGGLE';
            this.values = {'true-value':'group', 'false-value': 'noGroup'}
        }
        return selectGroupOptionsInstance;
    }
}