let hasBackDropInstance = null;
module.exports = class hasBackDrop {

    constructor() {
        if (!hasBackDropInstance) {
            hasBackDropInstance = this;
            //set all other default values

            this.displayAs = 'Has Back Drop';
            this.value = 'hasBackdrop'
            this.type = 'TOGGLE';
            this.values = {'true-value':'true', 'false-value': 'false'}
        }
        return hasBackDropInstance;
    }
}