let hasErrorInstance = null;
module.exports = class hasError {

    constructor() {
        if (!hasErrorInstance) {
            hasErrorInstance = this;
            //set all other default values

            this.displayAs = 'Has Error';
            this.value = 'hasError'
            this.type = 'TOGGLE';
            this.values = {'true-value':'true', 'false-value': 'false'}
        }
        return hasErrorInstance;
    }
}