let SelectHiderequiredMarker = null;
module.exports = class hiderequiredMarker {

    constructor() {
        if (!SelectHiderequiredMarker) {
            SelectHiderequiredMarker = this;
            //set all other default values

            this.displayAs = 'Hide required marker';
            this.value = '[hideRequiredMarker]'
            this.type = 'TOGGLE';
            this.values = { 'true-value': 'true', 'false-value': 'false' }
        }
        return SelectHiderequiredMarker;
    }
}