let hideToggleInstance = null;
module.exports = class hideToggle {

    constructor() {
        if (!hideToggleInstance) {
            hideToggleInstance = this;
            //set all other default values

            this.displayAs = 'Hide Toggle';
            this.value = 'hideToggle'
            this.type = 'TOGGLE';
            this.values = {'true-value':'true', 'false-value': 'false'}
        }
        return hideToggleInstance;
    }
}