let indeterminateInstance = null;
module.exports = class indeterminate {

    constructor() {
        if (!indeterminateInstance) {
            indeterminateInstance = this;
            //set all other default values

            this.displayAs = 'Indeterminate';
            this.value = '[indeterminate]'
            this.type = 'TOGGLE';
            this.values = {'true-value':'true', 'false-value': 'false'}
        }
        return indeterminateInstance;
    }
}