let inputFloatLabelInstance = null;
module.exports = class FloatLabel {

    constructor() {
        if (!inputFloatLabelInstance) {
            inputFloatLabelInstance = this;
            //set all other default values

            this.displayAs = 'Input Float Label ';
            this.value = '[floatLabel]'
            this.type = 'DROPDOWN';
            this.values = [
                { display: "Auto", value: "'auto'" },
                { display: "Always ", value: "'always'"},
                { display: "Never", value: "'never'" }
            ];
        }

        return inputFloatLabelInstance;
    }
}