let InputHintAlignInstance = null;
module.exports = class InputHintAlign {

    constructor() {
        if (!InputHintAlignInstance) {
            InputHintAlignInstance = this;
            //set all other default values

            this.displayAs = 'Input Hint Align';
            this.value = 'hintAlign'
            this.type = 'TOGGLE';
            this.labels = {'true-label': 'Start', 'false-label': 'End'}
            this.values = {'true-value':'start', 'false-value': 'end'}
        }
        return InputHintAlignInstance;
    }
}
