let isActiveInstance = null;
module.exports = class isActive {

    constructor() {
        if (!isActiveInstance) {
            isActiveInstance = this;
            //set all other default values

            this.displayAs = 'isActive';
            this.value = '[isActive]'
            this.type = 'TOGGLE';
            this.values = {'true-value':'true', 'false-value': 'false'}
        }
        return isActiveInstance;
    }
}