let ImgSecureInstance = null;
module.exports = class ImgSecure {

    constructor() {
        if (!ImgSecureInstance) {
            ImgSecureInstance = this;
            //set all other default values

            this.displayAs = 'Secure URI';
            this.value = 'imgSecure'
            this.type = 'TOGGLE';
            this.values = {'true-value': 'artImgSrc', 'false-value': ''}
        }
        return ImgSecureInstance;
    }
}
