let LayoutAlignInstance = null;
module.exports = class LayoutAlign {

    constructor() {
        if (!LayoutAlignInstance) {
            LayoutAlignInstance = this;
            //set all other default values

            this.displayAs = 'Layout Direction';
            this.value = 'layoutAlign'
            this.type = 'DROPDOWN';
            this.values = [
                { display: "none", value: "none" },
                { display: "start", value: "start", default: true },
                { display: "center", value: "center" },
                { display: "end", value: "end" },
                { display: "space-around", value: "space-around" },
                { display: "space-between", value: "space-between" },
                { display: "space-evenly", value: "space-evenly" }
            ];
        }

        return LayoutAlignInstance;
    }
}