let LazyLoadingInstance = null;
module.exports = class lazyLoading {

    constructor() {
        if (!LazyLoadingInstance) {
            LazyLoadingInstance = this;
            //set all other default values

            this.displayAs = 'Enable Lazy Loading';
            this.value = 'lazyLoading'
            this.type = 'TOGGLE';
            this.values = {'true-value':'true', 'false-value': 'false'}
        }
        return LazyLoadingInstance;
    }
}